mapper.select:
SELECT num, name, tel, building_num, title, password
FROM staff_info
WHERE tel = #{tel}
    AND password = #{password}

mapper.update:
UPDATE staff_info
SET password = #{newPassword}
WHERE tel = #{tel}
    AND password = #{oldPassword}

mapper.select:
SELECT tel
FROM staff_info
WHERE tel = #{tel}

mapper.update:
UPDATE staff_info
SET tel = #{newTel}
WHERE tel = #{oldTel}
    AND password = #{password}
